package	com.geopista.app.utilidades.um.event;

import	java.util.EventObject;

/**
 * This class is to support TimeListener. All the details of the events
 * generated by this listener are stored in the object of this class.
 * 
 * @version	1.0 06/08/99
 * @author	UnicMan
 */
public class TimeEvent
	extends		EventObject
{
	private String	mszText;
	
	public TimeEvent( Object oSource, String szText )
	{
		super( oSource );
		mszText = szText;
	}

	/**
	 * Returns the invalid text that was entered by the user in the textfield.
	 *
	 * @return	text entered by the user
	 */
	public String getInvalidText()
	{
		return	mszText;
	}
}
